$.fn.starRating=function(){"use strict";return this.each(function(){var e=$(this);var t=e.data("amount").toString();var n=t.split(".");var r=n[0];for(var i=1;i<=r;i++){$("<div/>",{"class":"star"}).appendTo(e);if(i==5){break}}if(n[1]&&r<5){var s=Math.round(Number("."+n[1])*2)/2;var o=s==.5?"star half":"star";if(s>0){$("<div/>",{"class":o}).appendTo(e)}}})}